class Vecteur2D:
    def __init__(self, x, y):
        self.x = x
        self.y = y

    def __abs__(self):
        # Calcule la norme euclidienne
        return (self.x**2 + self.y**2) ** 0.5

    def __repr__(self):
        return f"Vecteur2D({self.x}, {self.y})"

# Création d'un vecteur
v = Vecteur2D(3, 4)
print(v)        # Vecteur2D(3, 4)
print(abs(v))   # 5.0

# Exemple d'utilisation avancée de abs()
class Temperature:
    def __init__(self, celsius):
        self.celsius = celsius

    def __sub__(self, other):
        # Retourne la différence entre deux températures
        return Temperature(self.celsius - other.celsius)

    def __abs__(self):
        # Retourne l'écart par rapport à 0°C
        return abs(self.celsius)

    def __repr__(self):
        return f"{self.celsius}°C"

# Calculer l'écart entre deux températures
t1 = Temperature(5)   # 5°C
t2 = Temperature(-3)  # -3°C
diff = abs(t1 - t2)   # abs(8°C) = 8
print(f"Écart de température: {diff}°C")

