import React, { useState, useEffect } from 'react';

function UserProfile({ userId }) {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // S'ex√©cute uniquement quand userId change
  useEffect(() => {
    setLoading(true);
    fetch(`https://api.example.com/users/${userId}`)
      .then(response => response.json())
      .then(data => {
        setUser(data);
        setLoading(false);
      });
  }, [userId]); // Tableau de d√©pendances

  if (loading) return <p>Chargement...</p>;
  if (!user) return <p>Utilisateur non trouv√©</p>;

  return (
    <div>
      <h1>{user.name}</h1>
      <p>Email: {user.email}</p>
    </div>
  );
}

import React, { useState, useEffect } from 'react';

function WindowWidth() {
  const [width, setWidth] = useState(window.innerWidth);

  useEffect(() => {
    // D√©finition de l'effet
    const handleResize = () => setWidth(window.innerWidth);
    window.addEventListener('resize', handleResize);

    // Fonction de nettoyage (cleanup)
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []); // Tableau vide = ex√©cution une seule fois (montage)

  return <p>Largeur de la fen√™tre: {width}px</p>;
}

