examples.code:
export default {
  name: 'TodoItem',

  // Props du composant
  props: {
    todo: {
      type: Object,
      required: true
    },
    index: Number
  },

  // √âmissions d√©clar√©es
  emits: ['toggle', 'remove'],

  // √âtat r√©actif
  data() {
    return {
      isEditing: false,
      editText: this.todo.text
    }
  },

  // Propri√©t√©s calcul√©es
  computed: {
    itemClass() {
      return {
        'completed': this.todo.done,
        'editing': this.isEditing
      }
    }
  },

  // M√©thodes
  methods: {
    toggleComplete() {
      this.$emit('toggle', this.index)
    },

    startEdit() {
      this.isEditing = true
      this.editText = this.todo.text
      this.$nextTick(() => {
        this.$refs.editInput.focus()
      })
    },

    remove() {
      this.$emit('remove', this.index)
    }
  },

  // Watchers
  watch: {
    todo(newTodo) {
      this.editText = newTodo.text
    }
  },

  // Hooks de cycle de vie
  mounted() {
    console.log(`TodoItem ${this.index} mont√©`)
  }
}

// Options API
export default {
  data() {
    return {
      count: 0,
      doubled: 0
    }
  },
  methods: {
    increment() {
      this.count++
    }
  },
  computed: {
    doubled() {
      return this.count * 2
    }
  },
  mounted() {
    console.log('Component is mounted!')
  }
}

// Composition API √©quivalente
<script setup>
import { ref, computed, onMounted } from 'vue'

const count = ref(0)
const doubled = computed(() => count.value * 2)

function increment() {
  count.value++
}

onMounted(() => {
  console.log('Component is mounted!')
})
</script>

